import Vue from 'vue'
import Vuex, { StoreOptions } from 'vuex'
import VuexPersistence from 'vuex-persist'

const vuexLocal = new VuexPersistence({
    storage: window.localStorage
})

Vue.use(Vuex)

interface Account {
    type: string,
    regon: string,
    email: string,
    password: string,
}

interface RootState {
    accounts: Account[];
    accountInfo: Account;
    activeUser: {
        name: string,
        id: number
    };
    estimate: Array<Object>;
    estimateInfo: {
        OwnerId: number,
        solo: boolean,
        multi: boolean,
        location: string,
        bathroom: number,
        livingroom: number,
        kitchen: number,
        bedroom: number,
        hall: number,
        basement: number,
        garage: number,
        room: number,
        wardrobe: number
    };
}

export default new Vuex.Store<RootState>( {
    state: {
        accounts: [],
        accountInfo: {
            type: '',
            regon: '',
            email: '',
            password: '',
        },
        activeUser: {
            name: '',
            id: -1
        },
        estimate: [],
        estimateInfo: {
            OwnerId: -1,
            solo: false,
            multi: false,
            location: '',
            bathroom: 0,
            livingroom: 0,
            kitchen: 0,
            bedroom: 0,
            hall: 0,
            basement: 0,
            garage: 0,
            room: 0,
            wardrobe: 0
        }
    },
    mutations: {
        addNewUser(state, info) {
            state.accountInfo.type = info.typeOfCustomer;
            if (state.accountInfo.type === 'regon') {
                state.accountInfo.regon = info.regon;
            }
            state.accountInfo.email = info.email;
            state.accountInfo.password = info.password;
            state.accounts.push(state.accountInfo);
        },
        activeUser(state, {user, id}:{user: string, id: number}) {
            state.activeUser.name = user;
            state.activeUser.id = id;
        },
        logout(state) {
            state.activeUser.name = '';
            state.activeUser.id = -1;
        },
        addNewEstimate(state, info) {
            state.estimateInfo.OwnerId = state.activeUser.id;
            state.estimateInfo.solo = info.solo;
            state.estimateInfo.multi = info.multi;
            state.estimateInfo.location = info.location;
            state.estimateInfo.bathroom = info.bathroom;
            state.estimateInfo.livingroom = info.livingroom;
            state.estimateInfo.kitchen = info.kitchen;
            state.estimateInfo.bedroom = info.bedroom;
            state.estimateInfo.hall = info.hall;
            state.estimateInfo.basement = info.basement;
            state.estimateInfo.garage = info.garage;
            state.estimateInfo.room = info.room;
            state.estimateInfo.wardrobe = info.wardrobe;
            state.estimate.push(state.estimateInfo);
        }
    },
    actions: {},
    modules: {},
    plugins: [vuexLocal.plugin]
})
